<template>
<div>
    <select class="select" v-model="selected">
        <option selected>모든 지역</option>
        <option>서울</option>
        <option>경기도</option>
        <option>부산</option>
        <option>인천</option>
        <option>대구</option>
        <option>대전</option>
        <option>광주</option>
        <option>울산</option>
        <option>세종시</option>
        <option>충청남도</option>
        <option>충청북도</option>
        <option>경상남도</option>
        <option>경상북도</option>
        <option>전라남도</option>
        <option>전라북도</option>
        <option>강원도</option>
        <option>제주도</option>
    </select>
</div>
</template>

<script>
import { mapGetters } from "vuex";

export default {
    name: "Region",
    data() {
        return {
            selected: "모든 지역",
        }
    },
    computed: {
        ...mapGetters({
            controlInfo: "control/getControlInfo",
        })
    },
    mounted() {
        this.selected = this.controlInfo["region"];
    },
    watch: {
        selected() {
            this.$emit("setRegion", this.selected);
        } 
    }
}
</script>

